//
function serializePathSeg(psi) {
    var buf = psi.pathSegTypeAsLetter;
    switch (psi.pathSegType) {
    case 2: // SVGPathSeg.PATHSEG_MOVETO_ABS
    case 4: // SVGPathSeg.PATHSEG_LINETO_ABS
        buf += ' ';
        buf += psi.x;
        buf += ' ';
        buf += psi.y;
        break;
    case 6: // SVGPathSeg.PATHSEG_CURVETO_CUBIC_ABS
        buf += ' ';
        buf += psi.x;
        buf += ' ';
        buf += psi.y;
        buf += ',';
        buf += psi.x1;
        buf += ' ';
        buf += psi.y1;
        buf += ',';
        buf += psi.x2;
        buf += ' ';
        buf += psi.y2;
        break;
    case 1: // SVGPathSeg.PATHSEG_CLOSEPATH
        break;
    }
    buf += ' ';
    return buf;
}
//
function normalizedPathTest() {
var expected = 'M 118.5 102.5 C 159 62,118.5 80.168,136.668 62 L 303 62 '
        + 'C 343.5 102.5,325.33 62,343.5 80.17 '
        + 'L 343.5 150.5 '
        + 'C 313.48 171.67,343.5 158.74,327.33 167.05 '
        + 'C 231 183,291.56 178.98,262.27 183 '
        + 'C 148.52 171.67,199.73 183,170.44 178.98 '
        + 'C 118.5 150.5,134.67 167.05,118.5 158.74 '
        + 'z '
        + 'M 118.5 230.5 '
        + 'L 118.5 221.98 '
        + 'C 143.77 233.9,124.47 226.12,132.65 230.19 '
        + 'C 231 246,167.18 241.7,198.16 246 '
        + 'C 318.23 233.9,263.84 246,294.82 241.7 '
        + 'C 343.5 221.98,329.35 230.19,337.53 226.12 '
        + 'L 343.5 230.5 '
        + 'C 303 271,343.5 252.83,325.33 271 '
        + 'L 159 271 '
        + 'C 118.5 182.5,136.67 223,118.5 204.83 z ';
    var path = document.getElementById("path1");
    var norm;
    if (path.normalizedPathSegList) {
        norm = path.normalizedPathSegList;
    } else {
        setTestResult('skipped');
        return;
    }
    var result = '';
    var n = norm.numberOfItems;
    for (var i = 0; i < n; i++) {
        var itemnps = serializePathSeg(norm.getItem(i));
        result += itemnps;
    }
    assertEquals(expected, result);
}
//
function assertEquals(expected, actual) {
    if (expected == actual) {
        setTestResult('passed');
    } else {
        //console.log('Actual: ' + actual);
    }
}
//
function setTestResult(result) {
   var resultElm = document.getElementById("testResult");
   resultElm.setAttributeNS(null, "result", result);
}
